import React, { useState } from 'react';
import Login from './components/Login';
import HomePage from './components/HomePage';
import StepOne from './components/StepOne';
import StepTwo from './components/StepTwo';
import StepThree from './components/StepThree';

// Demo user data
const DEMO_USER = {
  email: "demo@example.com",
  password: "demo1234",
  username: "DemoUser",
  companyName: "Demo Company Ltd",
  firstName: "Demo",
  lastName: "User",
  mobile: "9876543210",
  residencePincode: "500070",
  companyPincode: "500070",
  gstn: "DEMO123456",
  companyAddress: "3-120, Hyderabad",
  city: "Hyderabad",
  state: "Telangana"
};

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // 'login', 'register', 'home'
  const [currentStep, setCurrentStep] = useState(1);
  const [registeredUsers, setRegisteredUsers] = useState([DEMO_USER]);
  const [currentUser, setCurrentUser] = useState(null);

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    companyName: '',
    firstName: '',
    lastName: '',
    mobile: '',
    residencePincode: '',
    companyPincode: '',
    gstn: '',
    companyAddress: '',
    city: '',
    state: 'Telangana'
  });

  const handleLogin = (email, password) => {
    const user = registeredUsers.find(
      u => u.email === email && u.password === password
    );

    if (user) {
      setCurrentUser(user);
      setCurrentPage('home');
      return { success: true };
    } else {
      return { success: false, error: 'Invalid email or password' };
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage('login');
  };

  const handleRegistrationComplete = (userData) => {
    setRegisteredUsers(prev => [...prev, userData]);
    setCurrentUser(userData);
    setCurrentPage('home');

    // Reset form
    setFormData({
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
      companyName: '',
      firstName: '',
      lastName: '',
      mobile: '',
      residencePincode: '',
      companyPincode: '',
      gstn: '',
      companyAddress: '',
      city: '',
      state: 'Telangana'
    });
    setCurrentStep(1);

    console.log('Registration Successful!', userData);
  };

  const navigateToRegister = () => {
    setCurrentPage('register');
    setCurrentStep(1);
  };

  const navigateToLogin = () => {
    setCurrentPage('login');
  };

  // Render current page
  if (currentPage === 'login') {
    return (
      <Login 
        onLogin={handleLogin}
        onNavigateToRegister={navigateToRegister}
      />
    );
  }

  if (currentPage === 'home') {
    return (
      <HomePage 
        user={currentUser}
        onLogout={handleLogout}
      />
    );
  }

  // Registration flow
  if (currentPage === 'register') {
    if (currentStep === 1) {
      return (
        <StepOne
          formData={formData}
          setFormData={setFormData}
          onNext={() => setCurrentStep(2)}
          onNavigateToLogin={navigateToLogin}
        />
      );
    }

    if (currentStep === 2) {
      return (
        <StepTwo
          formData={formData}
          setFormData={setFormData}
          onNext={() => setCurrentStep(3)}
          onPrevious={() => setCurrentStep(1)}
        />
      );
    }

    if (currentStep === 3) {
      return (
        <StepThree
          formData={formData}
          onComplete={handleRegistrationComplete}
          onPrevious={() => setCurrentStep(2)}
        />
      );
    }
  }

  return null;
}

export default App;
