import React, { useState, useEffect } from "react";
import axios from "axios";

import Login from "./components/Login";
import HomePage from "./components/HomePage";
import StepOne from "./components/StepOne";
import StepTwo from "./components/StepTwo";
import StepThree from "./components/StepThree";
import StepFour from "./components/StepFour";

function App() {
  const [currentPage, setCurrentPage] = useState("login");
  const [currentStep, setCurrentStep] = useState(1);
  const [currentUser, setCurrentUser] = useState(null);

  const [formData, setFormData] = useState({
    mobile: "",
    password: "",
    email: "",
    company_name: "",
    organisation_name: "",
    gst_number: "",
    authorised_person_name: "",
    disclaimer_accepted: false,
    address: "",
    city: "",
    state: "",
    pincode: ""
  });

  // ðŸ” Auto-login if token exists
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setCurrentPage("home");
    }
  }, []);

  // ðŸ” LOGIN
  const handleLogin = async (mobile, password) => {
    try {
      const res = await axios.post(
        "http://localhost:8000/api/auth/login/",
        { mobile, password }
      );

      localStorage.setItem("token", res.data.token);
      setCurrentUser(res.data.user);
      setCurrentPage("home");
      return { success: true };

    } catch (err) {
      return {
        success: false,
        error: err.response?.data?.message || "Login failed"
      };
    }
  };

  // ðŸ“ REGISTER
  const handleRegistrationComplete = async () => {
    try {
      await axios.post(
        "http://localhost:8000/api/auth/register/",
        formData
      );

      alert("Registration successful. Please login.");
      setCurrentPage("login");
      setCurrentStep(1);

    } catch (err) {
      alert(JSON.stringify(err.response?.data || "Registration failed"));
    }
  };

  // ðŸšª LOGOUT
  const handleLogout = () => {
    localStorage.removeItem("token");
    setCurrentUser(null);
    setCurrentPage("login");
  };

  return (
    <>
      {currentPage === "login" && (
        <Login
          onLogin={handleLogin}
          onNavigateToRegister={() => {
            setCurrentPage("register");
            setCurrentStep(1);
          }}
        />
      )}

      {currentPage === "home" && (
        <HomePage user={currentUser} onLogout={handleLogout} />
      )}

      {currentPage === "register" && currentStep === 1 && (
        <StepOne
          formData={formData}
          setFormData={setFormData}
          onNext={() => setCurrentStep(2)}
        />
      )}

      {currentPage === "register" && currentStep === 2 && (
        <StepTwo
          formData={formData}
          setFormData={setFormData}
          onNext={() => setCurrentStep(3)}
          onPrevious={() => setCurrentStep(1)}
        />
      )}

      {currentPage === "register" && currentStep === 3 && (
        <StepThree
          formData={formData}
          setFormData={setFormData}
          onNext={() => setCurrentStep(4)}
          onPrevious={() => setCurrentStep(2)}
        />
      )}

      {currentPage === "register" && currentStep === 4 && (
        <StepFour
          formData={formData}
          onComplete={handleRegistrationComplete}
          onPrevious={() => setCurrentStep(3)}
        />
      )}
    </>
  );
}

export default App;
