function HomePage({ user, onLogout }) {
  if (!user) return null;

  return (
    <div className="home-container">
      <div className="navbar">
        <h2>Manufacturer Portal</h2>
        <button onClick={onLogout}>Logout</button>
      </div>

      <h3>Welcome, {user.authorised_person_name} ðŸ‘‹</h3>

      <div className="info-card">
        <p><b>Company:</b> {user.organisation_name}</p>
        <p><b>GST Number:</b> {user.gst_number}</p>
        <p><b>Mobile:</b> {user.user_mobile}</p>
        <p><b>Email:</b> {user.user_email || "Not Provided"}</p>
        <p><b>City:</b> {user.city}</p>
        <p><b>State:</b> {user.state}</p>
        <p><b>Pincode:</b> {user.pincode}</p>
        <p><b>Status:</b> {user.status}</p>
      </div>
    </div>
  );
}

export default HomePage;
