import React, { useState } from 'react';

function StepOne({ formData, setFormData, onNext, onNavigateToLogin }) {
  const [errors, setErrors] = useState({});

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const validate = () => {
    const err = {};
    if (!/^\d{10}$/.test(formData.mobile)) err.mobile = "Valid mobile required";
    if (!formData.email) err.email = "Email required";
    if (!formData.password || formData.password.length < 8) err.password = "Min 8 characters";

    setErrors(err);
    return Object.keys(err).length === 0;
  };

  return (
    <div className="form-container">
      <h1>Account Information</h1>

      <input name="mobile" placeholder="Mobile" value={formData.mobile} onChange={handleChange}/>
      {errors.mobile && <p className="error-message">{errors.mobile}</p>}

      <input name="email" placeholder="Email" value={formData.email} onChange={handleChange}/>
      {errors.email && <p className="error-message">{errors.email}</p>}

      <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange}/>
      {errors.password && <p className="error-message">{errors.password}</p>}

      <button onClick={() => validate() && onNext()}>Next â†’</button>
      <p>Already have an account? <span onClick={onNavigateToLogin}>Login</span></p>
    </div>
  );
}

export default StepOne;
