import React, { useState, useEffect } from "react";
import axios from "axios";

import Login from "./components/Login";
import HomePage from "./components/HomePage";
import StepOne from "./components/StepOne";
import StepTwo from "./components/StepTwo";

function App() {
  const [currentPage, setCurrentPage] = useState("login");
  const [currentStep, setCurrentStep] = useState(1);
  const [currentUser, setCurrentUser] = useState(null);

  // âœ… FINAL formData (matches frontend + backend)
  const [formData, setFormData] = useState({
    // Step 1
    number: "",
    email: "",
    password: "",
    confirmPassword: "",
    companyName: "",

    // Step 2
    name: "",
    organizationName: "",
    gstn: "",
    companyAddress: "",
    companyPincode: "",
    city: "",
    state: ""
  });

  // ðŸ” Auto-login if token exists
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setCurrentPage("home");
    }
  }, []);

  // ðŸ” LOGIN
  const handleLogin = async (mobile, password) => {
    try {
      const res = await axios.post(
        "http://localhost:8000/api/auth/login/",
        { mobile, password }
      );

      localStorage.setItem("token", res.data.token);
      setCurrentUser(res.data.user);
      setCurrentPage("home");

      return { success: true };
    } catch (err) {
      return {
        success: false,
        error: err.response?.data?.message || "Login failed"
      };
    }
  };

  // ðŸ“ REGISTER (submit from Step 2)
  const handleRegistrationComplete = async () => {
    try {
      // âœ… Backend payload mapping
      const payload = {
        mobile: formData.number,
        email: formData.email,
        password: formData.password,

        organisation_name: formData.organizationName,
        gst_number: formData.gstn,
        authorised_person_name: formData.name,

        address: formData.companyAddress,
        city: formData.city,
        state: formData.state,
        pincode: formData.companyPincode,

        disclaimer_accepted: true
      };

      await axios.post(
        "http://localhost:8000/api/auth/register/",
        payload
      );

      alert("Registration successful. Please login.");
      setCurrentPage("login");
      setCurrentStep(1);

    } catch (err) {
      console.error(err.response?.data);
      alert(JSON.stringify(err.response?.data || "Registration failed"));
    }
  };

  // ðŸšª LOGOUT
  const handleLogout = () => {
    localStorage.removeItem("token");
    setCurrentUser(null);
    setCurrentPage("login");
  };

  return (
    <>
      {currentPage === "login" && (
        <Login
          onLogin={handleLogin}
          onNavigateToRegister={() => {
            setCurrentPage("register");
            setCurrentStep(1);
          }}
        />
      )}

      {currentPage === "home" && (
        <HomePage user={currentUser} onLogout={handleLogout} />
      )}

      {currentPage === "register" && currentStep === 1 && (
        <StepOne
          formData={formData}
          setFormData={setFormData}
          onNext={() => setCurrentStep(2)}
        />
      )}

      {currentPage === "register" && currentStep === 2 && (
        <StepTwo
          formData={formData}
          setFormData={setFormData}
          onNext={handleRegistrationComplete}
          onPrevious={() => setCurrentStep(1)}
        />
      )}
    </>
  );
}

export default App;
